import React, { Component } from 'react'
import {
  StyleSheet,
  TouchableOpacity,
  Text,
  View,
} from 'react-native'


class App extends Component {
  state = {
    endgame: false,
    move: "O",
    board: new Array(9),
    result: ""
  }

  WinConditions = [
    [0,1,2],
    [3,4,5],
    [6,7,8],
    [0,3,6],
    [1,4,7],
    [2,5,8],
    [0,4,8],
    [2,4,6]
  ];

  tmpBoard = new Array(9);

  updateBoard = () => {
      this.setState({board: this.tmpBoard});
  }

  isWinner = (board, move) => {
    for(let i=0; i<8; i++)
    {
      let allConds = true;
      for (let j=0; j<3; j++)
      {
        let p = this.WinConditions[i*3+j]
        if(board[p] !== move)
        {
          allConds = false;
          break;
        }
      }
      if (allConds)
      {
        alert(board);
        return true;
      } 
    }

    return false;

  }

  isDraw = (board) => {
    for(let i=0; i<board.length; i++)
    {
      if (!board[i]) return false;
    }
    return true;

  }

  isEndGame = (board, move) => {
    console.log("IsEndGame board:");
    console.log(board);
    if (this.isWinner(board, move))
    {
      this.setState({endgame: true});
      if (move == "O") this.setState({result: "You win!"});
      else this.setState({result: "You lost!"});
      return true;
    }
    else if (this.isDraw(board)) this.setState({endgame: true, result: "You draw!"});
    else return false;

  }

  botMove = () => {
    console.log("Bot move board:");
    console.log(this.tmpBoard);
    let legalMoves = Array();
    let loseThreads = Array();
    for (let i=0; i<9; i++)
    {
      if (!this.tmpBoard[i])
      {
        const board = [...this.tmpBoard];
        board[i] = "X";
        if (this.isWinner(board, "X"))
        {
          this.tmpBoard[i] = "X";
          return;
        }

        board[i] = "O";
        if (this.isWinner(board, "O"))
        {
          loseThreads.push(i);
        }
        legalMoves.push(i);

      }
    }
    let move = legalMoves[Math.floor(Math.random()*legalMoves.length)];
    if (loseThreads.length > 0) move = loseThreds[Math.floor(Math.random()*loseThreads.length)];
    this.tmpBoard[move] = "X";

  }

  onPress = (pos) => {
    if(!this.state.endgame && !this.state.board[pos] && this.state.move == "O")
    {
      this.tmpBoard[pos] = "O";
      if (this.isEndGame(this.tmpBoard, "O"))
      {
          this.updateBoard();
          return;
      }
      this.botMove();
      this.updateBoard();
      this.isEndGame(this.tmpBoard, "X");

    }
  }

  renderMessage(){
    if(this.state.endgame)
       return <Text>{this.state.result}</Text>;
    else
    {
      return <Text>Move: {this.state.move}</Text>
    }
    return null;
 }

 renderBoard(){
    let boxes = []
    for (let i=0; i<9; i++)
    {
      boxes.push(
      <TouchableOpacity style={styles.box} onPress={() => this.onPress(i)}>
        <Text style={styles.box_text}>{ this.state.board[i] }</Text>
      </TouchableOpacity>
      )
    }
    return <View style={styles.game_board}>{boxes}</View>
 }

 render() {
    return (
      <View style={styles.container}>
        <View style={styles.title}>
          <Text style={styles.title_text}>TicTacToe</Text>
        </View>

        <View style={styles.message}>
          { this.renderMessage() }
        </View>

        {this.renderBoard()}

      </View>

    )
  }
}

const styles = StyleSheet.create({
  container:
  {
    justifyContent: 'center',
    alignItems: 'center',
  },

  title:
  {
    marginTop: 10,
    paddingTop: 50,
    height: 100,
  },

  title_text:
  {
    fontSize: 50,
    fontWeight: "700"
  },

  game_board: {
    flexWrap: "wrap",
    flexDirection: "row",
    backgroundColor: "aliceblue",
    maxHeight: 360,
    marginTop: 100,
    maxWidth: 360,
    alignContent: 'center',
    borderRadius: 10,

  },
  box: {
    width: 120,
    height: 120,
    backgroundColor: "deepskyblue",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: 'white',
  },

  box_text: {
    color: "white",
    fontSize: 120,
    fontWeight: "700",
    textAlign: "center",
  }
})

export default App;
