import React, { Component } from 'react'
import {
  StyleSheet,
  TouchableOpacity,
  Text,
  View,
} from 'react-native'


class App extends Component {
  state = {
    endgame: false,
    move: "O",
    board: new Array(9),
    result: ""
  }

  WinConditions = [
    [0,1,2],
    [3,4,5],
    [6,7,8],
    [0,3,6],
    [1,4,7],
    [2,5,8],
    [0,4,8],
    [2,4,6]
  ];

  tmpBoard = new Array(9);

  makeMove = (pos) => {
    const board = [...this.state.board];
      board[pos] = this.state.move;
      this.setState({board: board}, () => {});
  }

  isWinner = (board, move) => {
    for(const condition in this.WinConditions)
    {
      for (const p in condition)
      {
        if(board[p] !== move) return false;
      }
    }
    return true;

  }

  isDraw = (board) => {
    for(let i=0; i<board.length; i++)
    {
      if (!board[i]) return false;
    }
    return true;

  }

  isEndGame = (board, move) => {
    if (this.isWinner(board, move))
    {
      this.setState({endgame: true});
      if (this.state.move == "O") this.setState({result: "You win!"});
      else this.setState({result: "You lost!"});
    }
    else if (this.isDraw(board)) this.setState({endgame: true, result: "You draw!"});
    else return false;
    return true;
  }

  botMove = () => {
    let legalMoves = Array();
    let loseThreads = Array();
    for (let i=0; i<9; i++)
    {
      if (!this.state.board[i])
      {
        const board = [...this.state.board]
        board[i] = this.state.move;
        if (this.isEndGame(board, "X"))
        {
          const board = [...this.state.board];
          board[i] = "X";
          this.setState({board: board}, () => {this.isEndGame(this.state.board)});
          return;
        }
        legalMoves.push(i);

      }
    }
    let move = legalMoves[Math.floor(Math.random()*legalMoves.length)];
    const board = [...this.state.board];
    board[move] = "X";
    this.setState({board: board, move: "O"}, () => {this.isEndGame(this.state.board)});

  }

  onPress = (pos) => {
    if(!this.state.endgame && !this.state.board[pos] && this.state.move == "O")
    {
      const board = [...this.state.board];
      board[pos] = "O";
      this.setState({board: board}, () => {
        if (this.isEndGame(this.state.board, "O"))
        {
          return;
        }
        this.setState({move: "X"}, () =>
        {
          this.botMove();
          // this.isEndGame(this.state.board);
        });

      });


    }
  }

  renderMessage(){
    if(this.state.endgame)
       return <Text>{this.state.result}</Text>;
    else
    {
      return <Text>Move: {this.state.move}</Text>
    }
    return null;
 }

 renderBoard(){
    let boxes = []
    for (let i=0; i<9; i++)
    {
      boxes.push(
      <TouchableOpacity style={styles.box} onPress={() => this.onPress(i)}>
        <Text style={styles.box_text}>{ this.state.board[i] }</Text>
      </TouchableOpacity>
      )
    }
    return <View style={styles.game_board}>{boxes}</View>
 }

 render() {
    return (
      <View style={styles.container}>
        <View style={styles.title}>
          <Text style={styles.title_text}>TicTacToe</Text>
        </View>

        <View style={styles.message}>
          { this.renderMessage() }
        </View>

        {this.renderBoard()}

      </View>

    )
  }
}

const styles = StyleSheet.create({
  container:
  {
    justifyContent: 'center',
    alignItems: 'center',
  },

  title:
  {
    marginTop: 10,
    paddingTop: 50,
    height: 100,
  },

  title_text:
  {
    fontSize: 50,
    fontWeight: "700"
  },

  game_board: {
    flexWrap: "wrap",
    flexDirection: "row",
    backgroundColor: "aliceblue",
    maxHeight: 360,
    marginTop: 100,
    maxWidth: 360,
    alignContent: 'center',
    borderRadius: 10,

  },
  box: {
    width: 120,
    height: 120,
    backgroundColor: "deepskyblue",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: 'white',
  },

  box_text: {
    color: "white",
    fontSize: 120,
    fontWeight: "700",
    textAlign: "center",
  }
})

export default App;
